-- 疾患に対応する傷病コードのある医科レセプトのSEQ2_NOを持つTABLE INIT_SEQ
CREATE  TABLE   INIT_SEQ(SEQ2_NO) AS
SELECT DISTINCT
    SEQ2_NO
FROM
    CMDV_T_RCP_MED_SY
WHERE
    PRAC_YM         >=  &START_YM
AND PRAC_YM         <=  &END_YM
AND VLD_FLG         =   1
